function debounce(func, delay) {
  let timer;
  return function (...args) {
    clearTimeout(timer);
    timer = setTimeout(() => func(...args), delay);
  };
}

// Search function with debounce
const handleSearchChange = debounce((query) => {
  console.log('Fetching results for:', query.toLowerCase());
}, 1000);
