import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const InfiniteScroll = () => {
  const [pokemonList, setPokemonList] = useState([]);
  const [offset, setOffset] = useState(0);
  const [limit] = useState(20);
  const [loading, setLoading] = useState(false);
  const observerRef = useRef(null);

  useEffect(() => {
    fetchPokemon();
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && !loading) {
          fetchPokemon();
        }
      },
      { threshold: 1.0 }
    );
    if (observerRef.current) {
      observer.observe(observerRef.current);
    }
    return () => observer.disconnect();
  }, [loading]);

  const fetchPokemon = async () => {
    if (loading) return;
    setLoading(true);
    try {
      const url = `https://pokeapi.co/api/v2/pokemon?limit=${limit}&offset=${offset}`;
      const response = await axios.get(url);
      setPokemonList((prevList) => [...prevList, ...response.data.results]);
      setOffset((prevOffset) => prevOffset + limit);
    } catch (error) {
      console.error('Error fetching Pokémon:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Infinite Scrolling - Pokémon List</h2>
      {pokemonList.map((pokemon, index) => (
        <div key={index} className="p-3 border-b border-gray-300">
          <h3 className="font-semibold">{pokemon.name}</h3>
        </div>
      ))}
      <div ref={observerRef} className="h-10" />
      {loading && <p className="text-center text-gray-500">Loading...</p>}
    </div>
  );
};

export default InfiniteScroll;
